import React from 'react';
import { useNavigate } from 'react-router-dom';
import weddingImage from '../styles/images/brave2.jpeg';
import weddingMusic from '../styles/music.mp3';

const Contentt = () => {
  const navigate = useNavigate();

  function playAudio() {
    const audio = new Audio(weddingMusic);
    audio.play();
  }

  return (
    <div className="content container">
      <div className="image-container">
        <img className="image" src={weddingImage} alt="" />
        <h1 className="judul">Contentt</h1>
        <button className="kembali" onClick={() => navigate("/")}>
          Cover
        </button>
        <button onClick={playAudio}>Play Sound</button>
        <p>
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Ullam vel
          quidem, magnam ut quas atque nobis quos provident, labore illo aut
          corrupti molestiae eaque enim earum minus repellendus, dolore alias.
        </p>
      </div>

      {/* Bagian lain dari komponen Anda di sini */}

      {/* Audio tag untuk pemutaran audio */}
      <audio id="background-audio" autoPlay loop>
        <source src={weddingMusic} type="audio/mpeg" />
      </audio>
    </div>
  );
};

export default Contentt;
