import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../styles/main.css";
import braveImage from "../styles/brave.png";
import Isi from "./isi";
export default function Cover() {
  const [isOpen, setIsOpen] = useState(false);

  const toggleContent = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div>
      <div className="container">
        <div className="divA">
          <div className="cover">
            <div className="judul1">The Wedding Of</div>
            <div className="judul2 nama-pasangan">Zaki & Nabila</div>
            <div className="quotes">
              “And I knew exactly how old Walt Disney’s Cinderella felt when she
              found her prince.”
              <br />
              — Elizabeth Young
            </div>
          </div>
        </div>
        <div className="divB center" id="contentDivB">
          {isOpen ? (
            <Isi />
          ) : (
            <div>
              <p>
                Lorem ipsum dolor sit amet consectetur adipisicing elit. Sequi non
                fugit consequuntur optio, repellat necessitatibus, labore illo nobis
                possimus iure explicabo hic voluptatem. Iste accusantium ipsam
                doloremque culpa assumenda mollitia?
              </p>
              <div className="image-container">
                <img src={braveImage} alt="" />
                <button id="bukaButton" onClick={toggleContent}>
                  Buka
                </button>
              </div>
              <p>
                Lorem ipsum, dolor sit amet consectetur adipisicing elit. Ad quasi
                ratione molestias consectetur non animi! Rem eaque doloremque eligendi
                quos placeat eum, alias, velit vero hic fugiat minus labore deleniti.
              </p>
              <br /><br />
              <p>Hello World</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
