import React, { Component } from 'react';
import weddingImage from '../styles/images/brave2.jpeg';

class Contentt extends Component {
  constructor(props) {
    super(props);
    this.state = { isPlaying: true };
  }

  toggleAudio = () => {
    const audio = document.getElementById('background-audio');
    if (this.state.isPlaying) {
      audio.pause();
    } else {
      audio.play();
    }
    this.setState({ isPlaying: !this.state.isPlaying });
  };

  render() {
    return (
      <div className="content container">
        <div className="image-container">
          <img className="image" src={weddingImage} alt="" />
          <h1 className="judul">Contentt</h1>
          
          <p>
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Ullam vel
            quidem, magnam ut quas atque nobis quos provident, labore illo aut
            corrupti molestiae eaque enim earum minus repellendus, dolore alias.
          </p>
        </div>

        <div className="quotes-container">
          <div className="box">
            <div className="image"></div>
            <p>
              Lorem ipsum dolor sit, amet consectetur adipisicing elit. Rem
              officiis nulla blanditiis totam aliquid repudiandae doloremque,
              facilis molestias, excepturi, nihil nam sunt cum aperiam non fuga
              accusamus cumque alias hic. Lorem ipsum dolor, sit amet consectetur
              adipisicing elit. Dolore consectetur sint itaque adipisci voluptates
              repellat? Dolore est maxime quae eius rem beatae! Iste illo,
              obcaecati animi nostrum error minus consequuntur.
            </p>
          </div>
        </div>

        {/* Sisipkan kode lainnya di sini */}
        <div className="video-container">
          <div className="video">
            <iframe
              width="560"
              height="315"
              src="https://www.youtube.com/embed/TDi-hGRYX6g?si=q8tz7BCs8cFxKTPP"
              title="YouTube video player"
              frameborder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
              allowfullscreen
            ></iframe>
            <div className="video-button">
              <h4>helloworld</h4>
              <button onClick={this.toggleAudio}>
                {this.state.isPlaying ? 'Pause Music' : 'Play Music'}
              </button>
            </div>
          </div>

          <div className="gift">
            <p>
              Lorem ipsum, dolor sit amet consectetur adipisicing elit. Eos natus
              nesciunt porro officia nemo veniam quas veritatis commodi numquam
              accusamus optio obcaecati itaque voluptatem impedit sit nihil, harum
              earum inventore.
            </p>
            <button>Tombol</button>
            <button>Tombol</button>
            <button>Tombol</button>
          </div>
        </div>
      </div>
    );
  }
}

export default Contentt;
